import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'

const ConfirmationPage = () => {

    let params = useParams(null)

    const [movie, setMovie] = useState({})
    const [screen, setScreen] = useState({})
    const [seat, setSeat] = useState({})
    const [ticket, setTicket] = useState({})

    let getBookingInfo = async () => {
        let response = await fetch(`api/movies/${params.id}/screen/${params.screenId}/booking/${params.bookingId}`)
        let data = await response.json()
        if (data.id == params.bookingId) {
            let ticketResponse = await fetch(`api/ticket/${data.ticket_type}`)
            let ticketData = await ticketResponse.json()
            setTicket(ticketData)
        }
    }

    let getMovieInfo = async () => {
        let response = await fetch(`api/movies/${params.id}`)
        let data = await response.json()
        if (data.id == params.id) {
            setMovie(data)
        }
    }

    let getScreenInfo = async () => {
        let response = await fetch(`api/movies/${params.id}/screen/${params.screenId}`)
        let data = await response.json()
        if (data.id == params.screenId) {
            setScreen(data)
        }
    }

    let getSeatInfo = async () => {
        let response = await fetch(`api/movies/${params.id}/screen/${params.screenId}/booking/${params.bookingId}/getSeatInfo`)
        let data = await response.json()
        if (data.id == params.bookingId) {
            setSeat(data)
        }
    }


    useEffect(() => {
        getBookingInfo()
        getMovieInfo()
        getScreenInfo()
        getSeatInfo()
    }, [])


    return (
    <div>
        <h3>Ticket Info:</h3>
        <div>Movie: {movie.title}</div>
        <div>Date: {screen.date}</div>
        <div>Time: {screen.time}</div>
        <div>Seat: {seat.seatCode}</div>
        <div>Ticket: {ticket.ticket_type} - ${ticket.price}</div>
    </div>
    )
}

export default ConfirmationPage
