import React, { useState, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom'
import AuthContext from "../context/AuthContext";

const ListTicket = () => {

  let { authTokens } = useContext(AuthContext);

  const [booking, setBooking] = useState([])
  const [movie, setMovie] = useState([])
  const [screen, setScreen] = useState([])
  const [seat, setSeat] = useState([])
  const [ticket, setTicket] = useState([])
  const [allData, setAllData] = useState({screen: {}, movie: {}, seat: {}, ticket: {}})

  let getBookingByUser = async () => {
    let response = await fetch(`api/user/booking`, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      }
    });
    let data = await response.json()

    data.forEach(item => {
      let getScreenData = async () => {
        let screenResponse = await fetch(`api/movies/${item.movie}/screen/${item.screening}`)
        let screenData = await screenResponse.json()
        setScreen(prevScreen => ([...prevScreen, screenData]))
        // setAllData(prevAllData => ([{...prevAllData, screen: screenData}]))
      }
      getScreenData()
    })

    data.forEach(item => {
      let getMovieData = async () => {
        let movieResponse = await fetch(`api/movies/${item.movie}`)
        let movieData = await movieResponse.json()
        setMovie(prevMovie => ([...prevMovie, movieData]))
        // setAllData(prevAllData => ([{...prevAllData, movie: movieData}]))
      }
      getMovieData()
    })

    data.forEach(item => {
      let getSeatData = async () => {
        let seatResponse = await fetch(`api/movies/${item.movie}/screen/${item.screening}/booking/${item.id}/getSeatInfo`)
        let seatData = await seatResponse.json()
        setSeat(preSeat => ([...preSeat, seatData]))
        // setAllData(prevAllData => ([{...prevAllData, seat: seatData}]))
      }
      getSeatData()
    })


    data.forEach(item => {
      let getTicketData = async () => {
        let ticketResponse = await fetch(`api/ticket/${item.ticket_type}`)
        let ticketData = await ticketResponse.json()
        setTicket(prevTicket => ([...prevTicket, ticketData]))
        // setAllData(prevAllData => ([{...prevAllData, ticket: ticketData}]))
      }
      getTicketData()
    })

  }

  useEffect(()=> {
    getBookingByUser()
  },[])

  const movieIds = []

  const uniqueMovie = movie.filter(element => {
    const isDuplicate = movieIds.includes(element.id)

    if (!isDuplicate) {
      movieIds.push(element.id);
      return true
    }

    return false

  })


  const screenIds = []

  const uniqueScreen = screen.filter(element => {
    const isDuplicate = screenIds.includes(element.id)

    if(!isDuplicate) {
      screenIds.push(element.id)
      return true
    }

    return false

  })

  const seatIds = []

  const uniqueSeat = seat.filter(element => {
    const isDuplicate = seatIds.includes(element.id)

    if(!isDuplicate) {
      seatIds.push(element.id)
      return true
    }

    return false

  })

  const ticketIds = []

  const uniqueTicket = ticket.filter(element => {
    const isDuplicate = ticketIds.includes(element.id)

    if(!isDuplicate) {
      ticketIds.push(element.id)
      return true
    }

    return false

  })



  // console.log(movie);
  // console.log(screen);
  // console.log(seat);
  // console.log(ticket);

  useEffect(() =>{
    setAllData({movie: movie, screen: screen, seat: seat, ticket: ticket})
  }, [movie, screen, seat, ticket])

  console.log(allData);

  return (
    <div>
      {allData.movie}
    </div>
  )
}

export default ListTicket
