import React, { useEffect, useState, useContext } from 'react'
import AuthContext from "../context/AuthContext";
import ListTicket from '../components/ListTicket'

const ProfilePage = () => {

  let { authTokens } = useContext(AuthContext);

  const [screen, setScreen] = useState([])
  const [booking, setBooking] = useState([])
  const [ticket, setTicket] = useState([])

  let getBookingByUser = async () => {
    let response = await fetch(`api/user/booking`, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + String(authTokens.access),
      }
    });
    let data = await response.json()
    setBooking(data)
    data.forEach(item => {
      fetch(`/api/movies/0/screen/${item.screening}`)
      .then(res => res.json())
      .then(screenData => {
        setScreen(prevScreen => [...prevScreen, screenData])
      })
    })
  }

  useEffect(()=> {
    getBookingByUser()
    booking.forEach(bookingItem => {
      screen.forEach(screenItem => {
        setTicket(bookingItem, screenItem)
      })
    })
  },[booking])

console.log(ticket);


  return (
    <div className='ticket-list'>
        <h2 className='page-title'>All ticket</h2>
        <div className='ticket-detail'>
            {booking.map((book) => (
              <ListTicket book={book} key={book.id} />
            ))}
        </div>
    </div>
  )
}

export default ProfilePage
